function get_dict() {
	$cssDir = __DIR__ . '/lang/';
	$cssFiles = glob($cssDir . '/*.php');

	$output = array();

	foreach ($cssFiles as $file) {
		$lang = explode('.',basename($file))[0];
		include('lang/'.basename($file));
		$output[$lang] = $string;
	}
	
	return $output;
}

function get_string($key) {
	global $dict, $lang;
	
	return $dict[$lang][$key] ?? '[['.$key.']]';
}

function get_dir() {
	global $lang;
	
	return (in_array($lang,array('en'))) ? 'ltr' : 'rtl';
}

